CREATE OR REPLACE FUNCTION obter_tipo_causa ( NR_SEQ_TIPO_CAUSA_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(60);


BEGIN

select	substr(obter_desc_expressao(CD_EXP_TIPO_CAUSA,DS_TIPO_CAUSA),1,60) DS_TIPO_CAUSA
into STRICT	ds_retorno_w
from	qua_tipo_causa
where	nr_sequencia = NR_SEQ_TIPO_CAUSA_P;

return	ds_retorno_w;

end;
