CREATE OR REPLACE FUNCTION obter_tipo_centro_custo ( cd_centro_custo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_grupo_w	varchar(1);

BEGIN

select	coalesce(max(a.ie_tipo),'X')
into STRICT	ie_tipo_grupo_w
from	centro_custo a
where	a.cd_centro_custo = cd_centro_custo_p;

return ie_tipo_grupo_w;

end;
