CREATE OR REPLACE FUNCTION obter_tipo_classif_escala (nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_escala_w	varchar(10) := 'S';


BEGIN

if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then
	begin
	select	ie_tipo_escala
	into STRICT	ie_tipo_escala_w
	from	escala_classif e,
		escala_grupo d,
		escala c
	where 	c.nr_seq_grupo = d.nr_sequencia
	and	d.nr_seq_classif = e.nr_sequencia
	and	c.nr_sequencia = nr_seq_classif_p  LIMIT 1;
	exception
	when others then
		ie_tipo_escala_w := '';
	end;
end if;

return	ie_tipo_escala_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
