CREATE OR REPLACE FUNCTION obter_tipo_compl_guia_web (nr_seq_conta_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2) := 'CP';
nr_seq_regra_w			bigint;
ie_estagio_complemento_w	smallint;
ie_tipo_complemento_w		smallint;
/*
	ds_retorno_w:
	N : confirmação
	S:  complementar
*/
BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	max(nr_seq_regra_compl)
	into STRICT	nr_seq_regra_w
	from	pls_guia_plano
	where	nr_sequencia   = nr_seq_guia_p;

	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '')	then
		select	coalesce(max(ie_tipo_complemento),0)
		into STRICT	ie_tipo_complemento_w
		from	pls_regra_conta_compl
		where	nr_sequencia = nr_seq_regra_w;

		if (ie_tipo_complemento_w = 2) then
			ds_retorno_w := 'CF';
		end if;
	end if;
else
	select	coalesce(max(ie_estagio_complemento),0)
	into STRICT	ie_estagio_complemento_w
	from	pls_conta
	where	nr_sequencia = nr_seq_conta_p;

	if (ie_estagio_complemento_w = 4) then
		ds_retorno_w := 'CF';
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
