CREATE OR REPLACE FUNCTION obter_tipo_compl_pessoa (nr_sequencia_p bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
ie_complemento_w 	numeric(20);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		select ie_tipo_complemento
		into STRICT ie_complemento_w
		from COMPL_PESSOA_FISICA
		where nr_sequencia = nr_sequencia_p
		and CD_PESSOA_FISICA = cd_pessoa_fisica_p;
	--(1,2,3,4,5,6,8,99,9)(Residencial,Comercial,Responsável,Pai,Mãe,Cônjuge,Hospedagem,Todos,Adicional)
		if (ie_complemento_w = 1) then --Residencial
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659282);
		elsif (ie_complemento_w = 2) then --Comercial
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659283);
		elsif (ie_complemento_w = 3) then --Responsável
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659284);
		elsif (ie_complemento_w = 4) then --Pai
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659285);
		elsif (ie_complemento_w = 5) then --Mãe
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659286);
		elsif (ie_complemento_w = 6) then --Cônjuge
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659287);
		elsif (ie_complemento_w = 8) then --Hospedagem
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659288);
		elsif (ie_complemento_w = 9) then --Adicional
		ds_retorno_w := Wheb_mensagem_pck.get_texto(659289);
		end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
