CREATE OR REPLACE FUNCTION obter_tipo_comprobante (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	 varchar(10) := 'I';
ie_nota_credito_w  operacao_nota.ie_nota_credito%type;
vl_total_nota_w    nota_fiscal.vl_total_nota%type;
cd_operacao_nf_w   operacao_nota.cd_operacao_nf%type;


BEGIN

select coalesce(vl_total_nota,0),
       coalesce(cd_operacao_nf,0)
into STRICT   vl_total_nota_w,
       cd_operacao_nf_w
from   nota_fiscal
where  nr_sequencia = nr_sequencia_p;

if (vl_total_nota_w = 0) then
     ds_retorno_w := 'P';

elsif (cd_operacao_nf_w <> 0) then

	select coalesce(ie_nota_credito,'N')
	into STRICT   ie_nota_credito_w
	from   operacao_nota
	where  cd_operacao_nf = cd_operacao_nf_w;

	if (ie_nota_credito_w = 'S') then
		ds_retorno_w := 'E';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
