CREATE OR REPLACE FUNCTION obter_tipo_contagem_inventario (nr_seq_inventario_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ds_retorno_w	varchar(15) := 'I';

/*
Retorna o tipo de contagem do inventário, isto é, se a alteração da coluna de contagem será pelo próprio inventário ou pelo item do mesmo.
Retorno:
I - Inventário
T - Item
*/
BEGIN

select	count(*)
into STRICT	qt_existe_w
from	inventario
where	nr_sequencia = nr_seq_inventario_p;

if (qt_existe_w > 0) then
	begin

	select	coalesce(ie_tipo_contagem,'I')
	into STRICT	ds_retorno_w
	from	inventario
	where	nr_sequencia = nr_seq_inventario_p;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
