SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_conv_lib_desconto ( nr_internop_conta_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_regra_w		bigint;
ie_tipo_convenio_w	smallint;
ie_retorno_w		varchar(1) := 'S';
			

BEGIN 
 
select	count(*) 
into STRICT	qt_regra_w 
from	Tipo_Conv_Lib_Desconto 
where	ie_situacao		= 'A';
 
if (qt_regra_w > 0) then 
 
	select	b.ie_tipo_convenio 
	into STRICT	ie_tipo_convenio_w 
	from	conta_paciente a, 
		convenio b 
	where	a.cd_convenio_parametro = b.cd_convenio 
	and	a.nr_interno_conta	= nr_internop_conta_p 
	and	a.cd_estabelecimento	= cd_estabelecimento_p;
	 
	qt_regra_w	:= 0;
	 
	select	count(*) 
	into STRICT	qt_regra_w 
	from	Tipo_Conv_Lib_Desconto 
	where	ie_tipo_convenio 	= ie_tipo_convenio_w 
	and	ie_situacao		= 'A';
	 
	if (qt_regra_w > 0) then 
		ie_retorno_w := 'S';
	else	 
		ie_retorno_w := 'N';
	end if;
 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
