CREATE OR REPLACE FUNCTION obter_tipo_conv_ultimo_atend ( nr_atendimento_atual_p bigint, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


ie_tipo_convenio_w		smallint;
nr_atendimento_w		bigint;


BEGIN

begin
select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	atendimento_paciente
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_atendimento		<> nr_atendimento_atual_p
and	coalesce(dt_cancelamento::text, '') = ''
and	cd_estabelecimento = 	wheb_usuario_pck.get_cd_estabelecimento;
exception
	when others then
		nr_atendimento_w	:= 0;
end;

if (nr_atendimento_w > 0) then
	select	ie_tipo_convenio
	into STRICT	ie_tipo_convenio_w
	from	atendimento_paciente
	where	nr_atendimento		= nr_atendimento_w
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_estabelecimento	= wheb_usuario_pck.get_cd_estabelecimento;
end if;

return ie_tipo_convenio_w;

end;
$body$
