CREATE OR REPLACE FUNCTION obter_tipo_data_trib_dirf ( cd_tributo_p bigint, cd_darf_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


retorno_w 	smallint;


BEGIN

select	max(ie_tipo_data)
into STRICT	retorno_w
from	dirf_regra_tributo r
where	r.cd_tributo = cd_tributo_p
and	r.cd_darf = cd_darf_p
and	((r.cd_estabelecimento = cd_estabelecimento_p)
or (coalesce(r.cd_estabelecimento::text, '') = ''));

return	retorno_w;

end;
$body$
