CREATE OR REPLACE FUNCTION obter_tipo_destinatario ( cd_paciente_carta_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255) := '';
cd_pessoa_registro_w	varchar(255);
ie_medico_externo_w		bigint;


BEGIN
	select (select max(b.cd_pessoa_fisica) from carta_medica b where b.nr_sequencia = a.nr_seq_carta_mae) cd
	into STRICT	cd_pessoa_registro_w
	from	DESTINATARIO_CARTA_MEDICA a
	where	a.nr_sequencia = nr_sequencia_p;

	if (cd_pessoa_registro_w = cd_paciente_carta_p) then

		ds_retorno_w := obter_texto_tasy(1044563, null);

	elsif (obter_se_medico(cd_paciente_carta_p, 'M') = 'S') then

		select	count(1)
		into STRICT	ie_medico_externo_w
		from	PF_MEDICO_EXTERNO
		where	cd_medico = cd_paciente_carta_p
		and		cd_pessoa_fisica = cd_pessoa_registro_w;

		if (ie_medico_externo_w > 0) then

			select	max((	SELECT substr(ds_tipo_medico,1,255)
						FROM   tipo_medico_externo x
						WHERE  x.nr_sequencia = a.nr_seq_tipo_medico ))
			into STRICT	ds_retorno_w
			from	PF_MEDICO_EXTERNO a
			where	a.cd_medico = cd_paciente_carta_p
			and		a.cd_pessoa_fisica = cd_pessoa_registro_w;

		end if;

		if (ds_retorno_w = '' or coalesce(ds_retorno_w::text, '') = '') then

			select  CASE WHEN coalesce(max(b.cd_pessoa_fisica), 0)=0 THEN  CASE WHEN coalesce(max(cd_departamento), 0)=0 THEN  obter_texto_tasy(1026476, null)  ELSE obter_texto_tasy(1026473, null) END   ELSE obter_texto_tasy(1026475, null) END
			into STRICT 	ds_retorno_w
			FROM usuario a
LEFT OUTER JOIN medico b ON (a.cd_pessoa_fisica = b.cd_pessoa_fisica AND 'S' = b.ie_medico_senior)
LEFT OUTER JOIN departamento_medico c ON (a.cd_pessoa_fisica = c.cd_responsavel)
WHERE lower(a.nm_usuario) like lower(obter_usuario_pessoa(cd_paciente_carta_p));

		end if;
	else

		ds_retorno_w := obter_texto_tasy(1044569, null);

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
