CREATE OR REPLACE FUNCTION obter_tipo_devedor (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ie_tipo_devedor_w	varchar(255);
ds_retorno_w		varchar(255);
cd_tipo_portador_w	integer;
cd_portador_w		bigint;
ie_origem_titulo_w	varchar(10);
cd_convenio_w		integer;
nr_seq_terc_conta_w	bigint;
nr_seq_contrato_w	bigint;

c01 CURSOR FOR 
SELECT	ie_tipo_devedor 
from	regra_eis_titulo_venc 
where	coalesce(cd_tipo_portador,coalesce(cd_tipo_portador_w,0))	= coalesce(cd_tipo_portador_w,0) 
and	coalesce(cd_portador,coalesce(cd_portador_w,0))		= coalesce(cd_portador_w,0) 
and	coalesce(ie_origem_titulo,coalesce(ie_origem_titulo_w,0))	= coalesce(ie_origem_titulo_w,0) 
order by 
	coalesce(cd_tipo_portador,0), 
	coalesce(cd_portador,0), 
	coalesce(ie_origem_titulo,0);
	

BEGIN 
 
select	max(a.cd_tipo_portador), 
	max(a.cd_portador), 
	max(a.ie_origem_titulo), 
	coalesce(max(a.cd_convenio),0), 
	max(a.nr_seq_terc_conta), 
	max(a.nr_seq_contrato) 
into STRICT	cd_tipo_portador_w, 
	cd_portador_w, 
	ie_origem_titulo_w, 
	cd_convenio_w, 
	nr_seq_terc_conta_w, 
	nr_seq_contrato_w 
from	titulo_receber_v a 
where	a.nr_titulo	= nr_titulo_p;
 
if (cd_convenio_w <> 0) then 
	ds_retorno_w	:= substr(obter_nome_convenio(cd_convenio_w),1,40);
elsif (nr_seq_terc_conta_w IS NOT NULL AND nr_seq_terc_conta_w::text <> '')	 then 
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(309457); -- Contas de terceiros 
elsif (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(309458); -- Contratos 
else
	open C01;
	loop 
	fetch C01 into	 
		ie_tipo_devedor_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_retorno_w	:= ie_tipo_devedor_w;	
	end loop;
	close C01;
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
