CREATE OR REPLACE FUNCTION obter_tipo_devol_mat_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_devolucao_p bigint, nr_seq_item_devol_p bigint) RETURNS varchar AS $body$
DECLARE



ie_tipo_baixa_estoque_w		varchar(002);
ds_tipo_devolucao_w		varchar(100);


BEGIN
if (coalesce(nr_seq_prescr_p, 0) > 0) and (coalesce(nr_devolucao_p, 0) > 0) and (coalesce(nr_seq_item_devol_p, 0) > 0) then

	select	max(ie_tipo_baixa_estoque)
	into STRICT	ie_tipo_baixa_estoque_w
	from	item_devolucao_material_pac
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_prescricao	= nr_seq_prescr_p
	and	nr_devolucao		= nr_devolucao_p
	and	nr_sequencia		= nr_seq_item_devol_p;

/*Se tem a sequencia, é porque o item foi prescrito, então podemos buscar por este valor*/

elsif (coalesce(nr_seq_prescr_p, 0) > 0) then
	select	max(ie_tipo_baixa_estoque)
	into STRICT	ie_tipo_baixa_estoque_w
	from	item_devolucao_material_pac
	where	nr_prescricao			= nr_prescricao_p
	and	nr_sequencia_prescricao		= nr_seq_prescr_p;
/*Se não tem sequencia do item... quer dizer que não foi prescrito, então buscamos pelo numero da devolução*/

elsif (coalesce(nr_seq_prescr_p, 0) = 0) and (coalesce(nr_devolucao_p, 0) > 0) and (coalesce(nr_seq_item_devol_p, 0) > 0) then
	select	max(ie_tipo_baixa_estoque)
	into STRICT	ie_tipo_baixa_estoque_w
	from	item_devolucao_material_pac
	where	nr_devolucao		= nr_devolucao_p
	and	nr_sequencia		= nr_seq_item_devol_p;
end if;

if (ie_tipo_baixa_estoque_w IS NOT NULL AND ie_tipo_baixa_estoque_w::text <> '') then
	begin

	select	max(ds_tipo_baixa)
	into STRICT	ds_tipo_devolucao_w
	from 	tipo_baixa_prescricao
	where 	ie_prescricao_devolucao	= 'D'
	and	cd_tipo_baixa		= ie_tipo_baixa_estoque_w;
	end;
end if;


return	ds_tipo_devolucao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
