CREATE OR REPLACE FUNCTION obter_tipo_dialise ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_tipo_dialise_w	smallint;


BEGIN

select 	coalesce(max(CASE WHEN a.ie_tipo_dialise='P' THEN  5  ELSE CASE WHEN a.ie_tipo_dialise='H' THEN  6  ELSE 0 END  END ),0)
into STRICT 	nr_tipo_dialise_w
from 	hd_prescricao a,
	prescr_solucao b
where 	b.nr_seq_dialise 	= a.nr_sequencia
and 	a.nr_prescricao 	= nr_prescricao_p
and 	b.nr_seq_solucao 	= nr_seq_solucao_p;

return nr_tipo_dialise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
