CREATE OR REPLACE FUNCTION obter_tipo_docto_compras_proc ( nr_sequencia_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo_retorno_p
'0' = Descricao reduzida('S' ou 'O')
'1' = Descricao completa('Solicitacao de compras' ou 'Ordem de compras'*/
ie_tipo_documento_w		varchar(1);
ds_retorno_w			varchar(50);


BEGIN

select	max(ie_tipo_documento)
into STRICT	ie_tipo_documento_w
from (
	SELECT	max('S') ie_tipo_documento
	from	solic_compra_item
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	SELECT	max('O')
	from	ordem_compra_item
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	select	max('C')
	from	cot_compra_item
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	select	max('N')
	from	nota_fiscal_item
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	select	max('L')
	from	reg_lic_item
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	select	max('R')
	from	item_requisicao_material
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	select	max('G')
	from	contrato
	where	nr_seq_aprovacao = nr_sequencia_p
	
union all

	select	max('E')
	from	projeto_recurso
	where	nr_seq_aprovacao = nr_sequencia_p) alias9;

if (ie_tipo_retorno_p = '0') then
	ds_retorno_w := ie_tipo_documento_w;
elsif (ie_tipo_retorno_p = '1') then
	if (ie_tipo_documento_w = 'S') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306116) /*'Solicitacao de compras'*/
;
	elsif (ie_tipo_documento_w = 'O') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306118) /*'Ordem de compras'*/
;
	elsif (ie_tipo_documento_w = 'C') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306119) /*'Cotacao de compras'*/
;
	elsif (ie_tipo_documento_w = 'N') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306138) /*'Nota fiscal'*/
;
	elsif (ie_tipo_documento_w = 'L') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306139) /*'Registro licitacao'*/
;
	elsif (ie_tipo_documento_w = 'R') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306141) /*'Requisicao material'*/
;
	elsif (ie_tipo_documento_w = 'G') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(1170787) /*'Controle de Contratos'*/
;
	elsif (ie_tipo_documento_w = 'E') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(1206295) /*'Controle de Projetos e Recursos Externos'*/
;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
