SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_documento_compras ( nr_documento_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo_retorno_p
'0' = Descricao reduzida('S' ou 'O')
'1' = Descricao completa('Solicitacao de compras' ou 'Ordem de compras'*/
ie_tipo_documento_w		varchar(1);
ds_retorno_w			varchar(50);


BEGIN

select	max(ie_tipo_documento)
into STRICT	ie_tipo_documento_w
from (
	SELECT	'S' ie_tipo_documento
	from	solic_compra
	where	nr_solic_compra = nr_documento_p
	
union all

	SELECT	'O'
	from	ordem_compra
	where	nr_ordem_compra = nr_documento_p
	
union all

	select	'C'
	from	cot_compra
	where	nr_cot_compra = nr_documento_p) alias1;
	
if (coalesce(ie_tipo_documento_w,'X') = 'X') then
	select	max('R')
	into STRICT	ie_tipo_documento_w
	from	requisicao_material
	where	nr_requisicao = nr_documento_p;
end if;

if (coalesce(ie_tipo_documento_w,'X') = 'X') then
	select	max('N')
	into STRICT	ie_tipo_documento_w
	from	nota_fiscal
	where	nr_sequencia = nr_documento_p;
end if;

if (coalesce(ie_tipo_documento_w,'X') = 'X') then
	select	max('L')
	into STRICT	ie_tipo_documento_w
	from	reg_licitacao
	where	nr_sequencia = nr_documento_p;
end if;

if (coalesce(ie_tipo_documento_w,'X') = 'X') then
	select	max('G')
	into STRICT	ie_tipo_documento_w
	from	contrato
	where	nr_sequencia = nr_documento_p;
end if;
	
if (coalesce(ie_tipo_documento_w,'X') = 'X') then
	select	max('E')
	into STRICT	ie_tipo_documento_w
	from	projeto_recurso
	where	nr_sequencia = nr_documento_p;
end if;

if (ie_tipo_retorno_p = '0') then
	ds_retorno_w := ie_tipo_documento_w;
elsif (ie_tipo_retorno_p = '1') then
	if (ie_tipo_documento_w = 'S') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306116) /*'Solicitacao de compras'*/
;
	elsif (ie_tipo_documento_w = 'C') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(306119) /*'Cotacao de compras'*/
;
	elsif (ie_tipo_documento_w = 'R') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(149965) /*'Requisicao de Materiais'*/
;
	elsif (ie_tipo_documento_w = 'O') then	
		ds_retorno_w := wheb_mensagem_pck.get_texto(306118) /*'Ordem de compras'*/
;
	elsif (ie_tipo_documento_w = 'N') then	
		ds_retorno_w := wheb_mensagem_pck.get_texto(168903) /*'Nota fiscal'*/
;
	elsif (ie_tipo_documento_w = 'G') then	
		ds_retorno_w := wheb_mensagem_pck.get_texto(1170787) /*'Controle de Contratos'*/
;
	elsif (ie_tipo_documento_w = 'E') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(1206295) /*'Controle de Projetos e Recursos Externos'*/
;
	else	
		ds_retorno_w := wheb_mensagem_pck.get_texto(388955) /*'Licitacao'*/
;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_documento_compras ( nr_documento_p bigint, ie_tipo_retorno_p text) FROM PUBLIC;

