SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_doc_origem_proc ( nr_seq_aprovacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(2);
ie_ordem_w		varchar(1);
ie_solic_w		varchar(1);
nr_solic_compra_w		bigint;
ie_requisicao_w		varchar(1);
ie_cotacao_w		varchar(1);
ie_nota_w		varchar(1);
ie_licitacao_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_ordem_w
from	ordem_compra_item
where	nr_seq_aprovacao = nr_seq_aprovacao_p;

if (ie_ordem_w = 'S') then
	ie_retorno_w	:= 'OC';
else
	begin

	select	coalesce(max('S'),'N')
	into STRICT	ie_solic_w
	from	solic_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	if (ie_solic_w = 'S') then
		begin

		select	max(nr_solic_compra)
		into STRICT	nr_solic_compra_w
		from	solic_compra_item
		where	nr_seq_aprovacao = nr_seq_aprovacao_p;

		select	coalesce(ie_tipo_servico,'SC')
		into STRICT	ie_retorno_w
		from	solic_compra
		where	nr_solic_compra = nr_solic_compra_w;

		end;
	else
		begin

		select	coalesce(max('S'),'N')
		into STRICT	ie_requisicao_w
		from	item_requisicao_material
		where	nr_seq_aprovacao = nr_seq_aprovacao_p;

		if (ie_requisicao_w = 'S') then
			ie_retorno_w	:= 'RM';
		else
			begin

			select	coalesce(max('S'),'N')
			into STRICT	ie_cotacao_w
			from	cot_compra_item
			where	nr_seq_aprovacao = nr_seq_aprovacao_p;

			if (ie_cotacao_w = 'S') then
				ie_retorno_w	:= 'CC';
			else
				begin

				select	coalesce(max('S'),'N')
				into STRICT	ie_nota_w
				from	nota_fiscal_item
				where	nr_seq_aprovacao = nr_seq_aprovacao_p;

				if (ie_nota_w = 'S') then
					ie_retorno_w	:= 'NF';
				else
					begin
					select	coalesce(max('S'),'N')
					into STRICT	ie_licitacao_w
					from	reg_lic_item
					where	nr_seq_aprovacao = nr_seq_aprovacao_p;

					if (ie_licitacao_w = 'S') then
						ie_retorno_w	:= 'L';

					end if; /*Fim IR Licitação*/
					end;

				end if; /*Fim IF Nota*/
				end;
			end if; /*Fim IF Cotação*/
			end;
		end if; /*Fim IF Requisição*/
		end;
	end if; /*Fim IF Solicitação*/
	end;
end if; /*Fim IF Ordem Compra*/
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_doc_origem_proc ( nr_seq_aprovacao_p bigint) FROM PUBLIC;

