CREATE OR REPLACE FUNCTION obter_tipo_dosagem_atual ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_unidade_infusao_w	varchar(10);
nr_seq_evento_w		bigint;


BEGIN

Select	max(nr_sequencia)
into STRICT	nr_seq_evento_w
from	prescr_solucao_evento
where	nr_prescricao	= nr_prescricao_p
and 	nr_seq_solucao	= nr_seq_solucao_p
and	coalesce(ie_evento_valido,'S') = 'S'
and	(ie_tipo_dosagem IS NOT NULL AND ie_tipo_dosagem::text <> '');

if (nr_seq_evento_w IS NOT NULL AND nr_seq_evento_w::text <> '') then
	Select	max(ie_tipo_dosagem)
	into STRICT	ie_unidade_infusao_w
	from	prescr_solucao_evento
	where	nr_sequencia = nr_seq_evento_w;
end if;


return	ie_unidade_infusao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
