CREATE OR REPLACE FUNCTION obter_tipo_evol ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_evolucao_w	varchar(5):= '';
ds_evolucao_w		varchar(255):= '';

c01 CURSOR FOR
	SELECT	distinct substr(obter_valor_dominio(72,ie_tipo_evolucao),1,3) ds_tipo_evolucao
	from	evolucao_paciente
	where	nr_atendimento = nr_atendimento_p
	order by
		ds_tipo_evolucao;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	open c01;
		loop
		fetch c01 into	
			ds_tipo_evolucao_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */

			ds_evolucao_w := ds_evolucao_w || ', ' || ds_tipo_evolucao_w;
		end loop;
	close c01;
end if;

ds_evolucao_w := substr(ds_evolucao_w,3,length(ds_evolucao_w));

return ds_evolucao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
