CREATE OR REPLACE FUNCTION obter_tipo_fonte_prescr (cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_fonte_prescr_w varchar(3);



BEGIN

if (coalesce(cd_material_p,0) > 0) then

	select	max(ie_tipo_fonte_prescr)
