CREATE OR REPLACE FUNCTION obter_tipo_grupo_proced_bpa ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* Ie_opcao_p
 T - Tipo
 G - Grupo
*/
ie_tipo_atend_bpa_w	smallint;
ie_grupo_atend_bpa_w	smallint;
cd_procedimento_w	bigint;
cd_atividade_prof_bpa_w	smallint;
ie_retorno_w		smallint;


BEGIN

select	ie_tipo_atend_bpa,
	ie_grupo_atend_bpa,
	cd_procedimento,
	cd_atividade_prof_bpa
into STRICT	ie_tipo_atend_bpa_w,
	ie_grupo_atend_bpa_w,
	cd_procedimento_w,
	cd_atividade_prof_bpa_w
from	procedimento_paciente
where	nr_sequencia	= nr_sequencia_p;

if (coalesce(ie_tipo_atend_bpa_w::text, '') = '') or (coalesce(ie_grupo_atend_bpa_w::text, '') = '') then
	select	ie_tipo_atend_bpa,
		ie_grupo_atend_bpa
	into STRICT	ie_tipo_atend_bpa_w,
		ie_grupo_atend_bpa_w
	from	sus_atividade_prof_proc
	where	nr_sequencia	=	(SELECT	min(nr_sequencia)
					from	sus_atividade_prof_proc
					where	cd_atividade_profissional	= cd_atividade_prof_bpa_w
					and	cd_procedimento			= cd_procedimento_w);
end if;

if (ie_opcao_p	= 'T') then
	ie_retorno_w	:= ie_tipo_atend_bpa_w;
elsif (ie_opcao_p	= 'G') then
	ie_retorno_w	:= ie_grupo_atend_bpa_w;
end if;

return ie_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
