CREATE OR REPLACE FUNCTION obter_tipo_identificacao_pf ( cd_pessoa_fisica_p PESSOA_FISICA.CD_PESSOA_FISICA%type ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w PESSOA_FISICA.NR_CERT_NASC%type;


BEGIN

SELECT  MAX(CASE b.nr_seq_tipo_doc_identificacao
            WHEN 'CC' THEN a.nr_identidade
            WHEN 'CE' THEN a.nr_cartao_estrangeiro
            WHEN 'CD' THEN b.nr_cartao_diplomatico
            WHEN 'PA' THEN a.nr_passaporte
            WHEN 'SC' THEN b.nr_salvo_conduto
            WHEN 'PE' THEN a.nr_reg_geral_estrang
            WHEN 'RC' THEN a.nr_cert_nasc
            WHEN 'TI' THEN a.nr_ric
            WHEN 'CN' THEN a.cd_declaracao_nasc_vivo
            WHEN 'AS' THEN b.nr_adulto_sem_identificacao
            WHEN 'MS' THEN b.nr_menor_sem_identificacao
            END) DS_RETORNO
INTO STRICT    ds_retorno_w
FROM    pessoa_fisica a,
              pessoa_fisica_aux b
WHERE   a.cd_pessoa_fisica = b.cd_pessoa_fisica
AND     b.cd_pessoa_fisica = cd_pessoa_fisica_p;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
