CREATE OR REPLACE FUNCTION obter_tipo_indicacao ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_indicacao_w	bigint;
ds_retorno_w		varchar(40);

/* 
	ie_opcao_p	:	'C' - Código 
				'D' - Descrição		*/
 
 

BEGIN 
 
select	max(coalesce(nr_seq_indicacao,0)) 
into STRICT	nr_seq_indicacao_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
if (nr_seq_indicacao_w <> 0) then 
 
	if (ie_opcao_p = 'C') then 
		ds_retorno_w	:= substr(nr_seq_indicacao_w,1,40);
 
	elsif (ie_opcao_p = 'D') then 
		select	ds_indicacao 
		into STRICT	ds_retorno_w 
		from	tipo_indicacao 
		where	nr_sequencia	= nr_seq_indicacao_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
