CREATE OR REPLACE FUNCTION obter_tipo_indic_nrseq (nr_seq_indicacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2);
ie_pessoa_w	varchar(1);
ie_pj_w		varchar(1);
ie_midia_w	varchar(1);
ie_medico_w	varchar(1);
ie_medico_cc_w	varchar(1);


BEGIN

select 	ie_pessoa,
	ie_pessoa_juridica,
	ie_midia,
	ie_medico,
	ie_medico_clinico
into STRICT	ie_pessoa_w,
	ie_pj_w,
	ie_midia_w,
	ie_medico_w,
	ie_medico_cc_w
from 	tipo_indicacao
where 	nr_sequencia = (nr_seq_indicacao_p)::numeric;

if (ie_pessoa_w = 'S')  then
	ds_retorno_w := 'P';
elsif (ie_pj_w = 'S') then
	ds_retorno_w := 'J';
elsif (ie_midia_w = 'S') then
	ds_retorno_w := 'M';
elsif (ie_medico_w = 'S') then
	ds_retorno_w := 'N';
elsif (ie_medico_cc_w = 'S') then
	ds_retorno_w := 'CC';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
