CREATE OR REPLACE FUNCTION obter_tipo_isolamento_cih ( nr_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	b.nr_seq_tipo_isolamento 
	from	cih_tipo_isolamento a, 
		cih_atend_isolamento b 
	where	b.nr_seq_tipo_isolamento = a.nr_sequencia 
	and	b.nr_atendimento = nr_atendimento_p 
	order by coalesce(a.nr_seq_prioridade,9999) desc, 
		b.dt_atualizacao_nrec;
			
nr_seq_tipo_isolamento_w	bigint;
ie_paciente_isolado_w		varchar(1);
			

BEGIN 
 
select 	coalesce(max(ie_paciente_isolado),'N') 
into STRICT	ie_paciente_isolado_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
if (ie_paciente_isolado_w = 'S') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_tipo_isolamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		nr_seq_tipo_isolamento_w := nr_seq_tipo_isolamento_w;
		end;
	end loop;
	close C01;
end if;
 
return nr_seq_tipo_isolamento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
