SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_isolamento (cd_tipo_isolamento_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_retorno_p:
	CD	-> Código Isolamento
	DS	-> Descrição do isolamento
*/
cd_tipo_isolamento_w	varchar(10);
ds_tipo_isolamento_w	varchar(255);
ds_retorno_w		varchar(255);


BEGIN
if (cd_tipo_isolamento_p IS NOT NULL AND cd_tipo_isolamento_p::text <> '') then
	select	cd,
		ds
	into STRICT	cd_tipo_isolamento_w,
		ds_tipo_isolamento_w
	from (SELECT	vl_dominio cd,
			substr(ds_valor_dominio,1,60)||' '||wheb_mensagem_pck.get_texto(802904) ds,
			2
		from 	valor_dominio
		where	cd_dominio = 3360
		
union

		SELECT	to_char(a.nr_sequencia) cd,
			a.ds_tipo_isolamento ds,
			1
		from	cih_tipo_isolamento a
		order by	1,
				2) a
	where	cd = cd_tipo_isolamento_p;

	if (ie_retorno_p = 'CD') then
		ds_retorno_w	:= cd_tipo_isolamento_w;
	elsif (ie_retorno_p = 'DS') then
		ds_retorno_w	:= ds_tipo_isolamento_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_isolamento (cd_tipo_isolamento_p text, ie_retorno_p text) FROM PUBLIC;

