CREATE OR REPLACE FUNCTION obter_tipo_item_prescr (nr_atendimento_p prescr_mipres.nr_atendimento%type, nr_prescricao_p prescr_mipres.nr_prescricao%type) RETURNS varchar AS $body$
DECLARE


ds_return_w varchar(10);


BEGIN

  if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
    begin
      select max(ie_tipo_item_presc)
      into STRICT ds_return_w
      from prescr_mipres
      where nr_atendimento = nr_atendimento_p
      and nr_prescricao = nr_prescricao_p;
    exception
      when no_data_found then ds_return_w := null;
    end;
  end if;

  return ds_return_w;

end;
