CREATE OR REPLACE FUNCTION obter_tipo_item_resultado ( cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


/* Tipo de Item Resultado

D               Diárias
S               Taxas e Serviços
P               Exames e Procedimentos
M               Medicamentos
A               Alimentos
T               Materiais
C               Consignados
O               Outros
*/
ie_tipo_w			varchar(1);
ie_consignado_w		varchar(1);
ie_tipo_material_w	varchar(3);
ie_classificacao_w	varchar(1);


BEGIN

ie_tipo_w		:= 'O';

if (coalesce(cd_material_p,0) > 0) then
	begin
	select coalesce(max(ie_tipo_material),9),
		coalesce(max(ie_consignado),'0')
	into STRICT	ie_tipo_material_w,
		ie_consignado_w
	from	material
	where	cd_material = cd_material_p;
	if (ie_consignado_w <> '0') then
		ie_tipo_w		:= 'C';
	elsif (ie_tipo_material_w	= '5') then
		ie_tipo_w		:= 'A';
	elsif (ie_tipo_material_w	= '1') then
		ie_tipo_w		:= 'T';
	elsif (ie_tipo_material_w in ('2','3')) then
		ie_tipo_w		:= 'M';
	end if;
	end;
elsif (coalesce(cd_procedimento_p,0) > 0) then
	begin
	select coalesce(max(ie_classificacao),'1')
	into STRICT	ie_classificacao_w
	from	Procedimento
	where	cd_procedimento 	= cd_procedimento_p
	  and	ie_origem_proced	= ie_origem_proced_p;
	if (ie_classificacao_w = '3') then
		ie_tipo_w		:= 'D';
	elsif (ie_classificacao_w = '2') then
		ie_tipo_w		:= 'S';
	elsif (ie_classificacao_w = '1') then
		ie_tipo_w		:= 'P';
	end if;
	end;
end if;

RETURN ie_tipo_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
