CREATE OR REPLACE FUNCTION obter_tipo_justificativa_dev ( nr_devolucao_p bigint, nr_item_dev_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
C = Código
D = Descrição
*/
ds_retorno_w			varchar(255);
nr_seq_justificativa_w		bigint;
ds_justificativa_w		varchar(255);


BEGIN

select	coalesce(max(nr_seq_justificativa),0)
into STRICT	nr_seq_justificativa_w
from	item_devolucao_material_pac
where	nr_sequencia = nr_item_dev_p
and	nr_devolucao = nr_devolucao_p;

select	ds_justificativa
into STRICT	ds_justificativa_w
from	motivo_justificativa_dev
where	nr_sequencia = nr_seq_justificativa_w;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_justificativa_w;
else
	ds_retorno_w := nr_seq_justificativa_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
