CREATE OR REPLACE FUNCTION obter_tipo_lado_porte_cir ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/*	ie_opcao_p
	T - Tipo
	L - Lado
	P - Porte
*/
BEGIN
ds_retorno_w	:= '';

if (ie_opcao_p = 'T') then
	select	substr(obter_desc_tipo_cirurgia(cd_tipo_cirurgia),1,50)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where   nr_prescricao = nr_prescricao_p
	and	nr_sequencia  =  nr_sequencia_prescricao_p;
elsif (ie_opcao_p = 'L') then
	select	substr(obter_valor_dominio(1372,ie_lado),1,30)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where   nr_prescricao = nr_prescricao_p
	and	nr_sequencia  =  nr_sequencia_prescricao_p;
elsif (ie_opcao_p = 'P') then
	select	substr(obter_valor_dominio(1901,ie_porte),1,30)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where   nr_prescricao = nr_prescricao_p
	and	nr_sequencia  =  nr_sequencia_prescricao_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_lado_porte_cir ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint, ie_opcao_p text) FROM PUBLIC;

