CREATE OR REPLACE FUNCTION obter_tipo_lista_simpro_vig ( cd_simpro_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_tipo_lista_w	varchar(1);


BEGIN

if (coalesce(cd_simpro_p,0) > 0) then

	select	max(ie_tipo_lista)
	into STRICT	ie_tipo_lista_w
	from	simpro_preco
	where	cd_simpro = cd_simpro_p
	and	dt_vigencia = dt_vigencia_p;

end if;

return	ie_tipo_lista_w;

end;
$body$
LANGUAGE PLPGSQL
