CREATE OR REPLACE FUNCTION obter_tipo_material (cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
C - código;
D - Descrição;
*/
ie_tipo_material_w         varchar(3);
ds_retorno_w               varchar(255);

BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
   select  ie_tipo_material
   into STRICT    ie_tipo_material_w
   from    material
   where   cd_material = cd_material_p;
   ds_retorno_w    := ie_tipo_material_w;
   if (coalesce(ie_opcao_p,'C') = 'D') then
           select  obter_valor_dominio(29, ie_tipo_material_w)
           into STRICT    ds_retorno_w
;
   end if;
end if;
return     ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
