CREATE OR REPLACE FUNCTION obter_tipo_meta_estagio (nr_seq_estagio_p bigint, pr_estagio_p bigint, qt_horas_estagio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_meta_w	varchar(3);


BEGIN

select	coalesce(max(ie_tipo_meta),'D')
into STRICT	ie_tipo_meta_w
from 	regra_meta_estagio_audit
where 	nr_seq_estagio = nr_seq_estagio_p
and 	qt_horas_estagio_p between coalesce(qt_hora_inicial,0) and coalesce(qt_hora_final, qt_horas_estagio_p);
--and 	pr_estagio_p between pr_inicial and pr_final;  -- Esse % foi descontinuado (OS 737923)
return	ie_tipo_meta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
