SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_meta_suep (nr_seq_suep_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'A';
nr_seq_grupo_w		bigint;
qt_reg_w		bigint;
qt_reg_unica_w		bigint;
qt_reg_rec_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	suep_grupo
where	nr_seq_suep = nr_seq_suep_p
and	ie_grupo_suep = 'MC';

if (qt_reg_w > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_seq_grupo_w
	from	suep_grupo
	where	nr_seq_suep = nr_seq_suep_p
	and	ie_grupo_suep = 'MC';

	select	count(*)
	into STRICT	qt_reg_unica_w
	from	item_suep
	where	nr_seq_grupo = nr_seq_grupo_w
	and	ie_tipo_item = 'UN';

	select	count(*)
	into STRICT	qt_reg_rec_w
	from	item_suep
	where	nr_seq_grupo = nr_seq_grupo_w
	and	ie_tipo_item = 'RC';

	if 	(qt_reg_unica_w > 0 AND qt_reg_rec_w > 0) or
		(qt_reg_unica_w = 0 AND qt_reg_rec_w = 0)then
		ds_retorno_w	:= 'A';
	elsif (qt_reg_unica_w > 0) then
		ds_retorno_w	:= 'U';
	elsif (qt_reg_rec_w > 0) then
		ds_retorno_w	:= 'D';
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
