CREATE OR REPLACE FUNCTION obter_tipo_nota_conta (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ie_tipo_nota_w	varchar(20);


C01 CURSOR FOR
	SELECT 	nr_sequencia
	from 	nota_fiscal
	where 	nr_interno_conta = nr_interno_conta_p
	and 	ie_situacao = '1';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	if ( coalesce(ie_tipo_nota_w::text, '') = '') then
		ie_tipo_nota_w	:= obter_se_nota_entrada_saida(nr_sequencia_w);
	else
	  ie_tipo_nota_w	:= ie_tipo_nota_w || '-' || obter_se_nota_entrada_saida(nr_sequencia_w);
	end if;


	end;
end loop;
close C01;

return	ie_tipo_nota_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
