CREATE OR REPLACE FUNCTION obter_tipo_nota_ipergs (ie_tipo_atendimento_p bigint, ie_tipo_item_p bigint, cd_area_p bigint, cd_espec_p bigint, cd_grupo_p bigint, cd_item_p bigint, ie_origem_p bigint, nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


qt_regra_w	bigint;
tp_nota_w	integer := 0;
cd_estab_w	estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
qt_material_w    	bigint;
cd_material_regra_w    	material_atend_paciente.cd_material%type;


C01 CURSOR FOR
	SELECT	a.nr_tipo_nota,
		coalesce(b.cd_material,0)
	FROM regra_tipo_nota_ipe a
LEFT OUTER JOIN material_ref_tipo_nota_ipe b ON (a.nr_sequencia = b.nr_seq_regra_tipo_nota)
WHERE cd_estabelecimento					= cd_estab_w  and coalesce(cd_procedimento,coalesce(cd_item_p,0)) 			= coalesce(cd_item_p,0) and coalesce(cd_area_procedimento,cd_area_p)			= cd_area_p and coalesce(cd_especialidade,cd_espec_p)			= cd_espec_p and coalesce(cd_grupo_proc,cd_grupo_p)				= cd_grupo_p and coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))		= coalesce(ie_tipo_atendimento_p,0) order by coalesce(cd_procedimento,0),
		coalesce(ie_origem_proced,ie_origem_p),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0),
		coalesce(ie_tipo_atendimento,0);

BEGIN

if (ie_tipo_item_p = 1) then


	select	count(1)
	into STRICT	qt_regra_w
	from	regra_tipo_nota_ipe
	where	cd_estabelecimento = cd_estab_w;

	if (qt_regra_w > 0) then
		open C01;
		loop
		fetch C01 into
			tp_nota_w,
			cd_material_regra_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			select	count(1)
			into STRICT	qt_material_w
			from	material_atend_paciente
			where	nr_interno_conta = nr_interno_conta_p
			and	coalesce(cd_motivo_exc_conta::text, '') = ''
			and 	cd_material = cd_material_regra_w;

			if (cd_material_regra_w = 0) then
				tp_nota_w := tp_nota_w;
			elsif (qt_material_w > 0) then
				tp_nota_w := tp_nota_w;
			end if;

			end;
		end loop;
		close C01;
	end if;
end if;

return	tp_nota_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
