CREATE OR REPLACE FUNCTION obter_tipo_obs_conta_paciente ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
D - descrição do tipo da observação.
S - Sequencia do tipo.
*/
ds_retorno_w		varchar(255);
nr_seq_obs_w		bigint;
nr_seq_tipo_obs_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_obs_w
from	conta_paciente_observacao
where	nr_interno_conta = nr_interno_conta_p;

if (nr_seq_obs_w > 0) then
	select	coalesce(max(nr_seq_tipo_obs),0)
	into STRICT	nr_seq_tipo_obs_w
	from	conta_paciente_observacao
	where	nr_interno_conta = nr_interno_conta_p;

	if (ie_opcao_p = 'S') then
		ds_retorno_w := nr_seq_tipo_obs_w;
	elsif (ie_opcao_p = 'D') then
		select	substr(ds_tipo_observacao,1,255)
		into STRICT	ds_retorno_w
		from	tipo_observacao_conta
		where	nr_sequencia = nr_seq_tipo_obs_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
