CREATE OR REPLACE FUNCTION obter_tipo_ocorrencia_doacao (nr_seq_doacao_p bigint, ie_local_ocorrencia_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_w			varchar(100);
ds_tipo_ocorrencia_w	varchar(255);
ds_retorno_w		varchar(100);
nr_sequencia_w		bigint;

C01 CURSOR FOR
SELECT	 b.ds_cor,
	 substr(b.ie_prioridade || ' - '  || b.ds_tipo_ocorrencia,1,255),
		b.nr_sequencia
FROM san_motivo_ocorrencia a, san_tipo_ocorrencia b
LEFT OUTER JOIN san_ocorrencia_doador c ON (b.nr_sequencia = c.nr_seq_tipo_ocoreencia)
WHERE ((c.nr_seq_motivo_ocorrencia = a.nr_sequencia) or ((coalesce(c.nr_seq_motivo_ocorrencia::text, '') = '') and c.nr_seq_tipo_ocoreencia = b.nr_sequencia))  --and	 c.ie_local_ocorrencia 	= ie_local_ocorrencia_p
  and c.nr_seq_doacao 		= nr_seq_doacao_p order by b.ie_prioridade desc;


BEGIN

open C01;
loop
fetch C01 into
	ds_cor_w,
	ds_tipo_ocorrencia_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ie_tipo_p = 'C') then
		ds_retorno_w := ds_cor_w;
	elsif (ie_tipo_p = 'D') then
		ds_retorno_w := ds_tipo_ocorrencia_w;
	elsif (ie_tipo_p = 'SEQ') then
		ds_retorno_w := nr_sequencia_w;
	else
		ds_retorno_w := '';
	end if;

end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
