CREATE OR REPLACE FUNCTION obter_tipo_os ( dt_ordem_servico_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_tipo_os_w	varchar(3);
dt_ordem_servico_w	timestamp;

BEGIN

dt_ordem_servico_w	:= trunc(dt_ordem_servico_p);

if (clock_timestamp() - dt_ordem_servico_w >= 60) then
	ie_tipo_os_w	:= '0';
elsif (clock_timestamp() - dt_ordem_servico_w >= 45) then
	ie_tipo_os_w	:= '1';
elsif (clock_timestamp() - dt_ordem_servico_w >= 30) then
	ie_tipo_os_w	:= '2';
elsif (clock_timestamp() - dt_ordem_servico_w >= 15) then
	ie_tipo_os_w	:= '3';
elsif (clock_timestamp() - dt_ordem_servico_w >= 7) then
	ie_tipo_os_w	:= '4';
else
	ie_tipo_os_w	:= '5';
end if;

return	ie_tipo_os_w;

end;
$body$
