CREATE OR REPLACE FUNCTION obter_tipo_param_tabela_custo ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, nr_seq_parametro_p bigint, nr_seq_tabela_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(2);


BEGIN

if (coalesce(nr_seq_tabela_p::text, '') = '') then
select max(b.ie_tipo_parametro)
into STRICT	ie_retorno_w
from	tipo_tabela_custo_param b,
	tabela_custo a
where	a.cd_tipo_tabela_custo	= b.cd_tipo_tabela_custo
and	a.cd_estabelecimento		= cd_estabelecimento_p
and	a.cd_tabela_custo		= cd_tabela_custo_p
and	b.nr_seq_parametro		= nr_seq_parametro_p;
else
	select max(b.ie_tipo_parametro)
into STRICT	ie_retorno_w
from	tipo_tabela_custo_param b,
	tabela_custo a
where	a.cd_tipo_tabela_custo	= b.cd_tipo_tabela_custo
and	a.nr_sequencia	= nr_seq_tabela_p
and	b.nr_seq_parametro		= nr_seq_parametro_p;
end if;

return ie_retorno_w;

end;
