CREATE OR REPLACE FUNCTION obter_tipo_parecer_liberado (nr_atendimento_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w 			varchar(1) := 'S';
qt_reg_w	 			bigint;
cd_setor_atendimento_w	integer;
cd_perfil_w				integer;


BEGIN 
 
select 	count(*) 
into STRICT	qt_reg_w 
from 	tipo_parecer_liberacao 
where	nr_seq_tipo_parecer = nr_sequencia_p;
 
if (qt_reg_w > 0) then 
	begin 
	cd_setor_atendimento_w := Obter_Setor_Atendimento(nr_atendimento_p);
	cd_perfil_w			  := obter_perfil_ativo;
 
  select 	count(*) 
	into STRICT	qt_reg_w 
	from 	tipo_parecer_liberacao 
	where	nr_seq_tipo_parecer = nr_sequencia_p 
	and		coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w 
	and		coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;
 
  if (qt_reg_w = 0) then 
		ie_retorno_w := 'N';
	end if;
	end;
end if;
	 
return ie_retorno_w;
 
end;
$body$
