CREATE OR REPLACE FUNCTION obter_tipo_parto_suep (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_parto_normal_w	parto.ie_parto_normal%type;
ie_parto_cesaria_w	parto.ie_parto_cesaria%type;
ie_laqueadura_w 	parto.ie_laqueadura%type;
ie_parto_forceps_w	parto.ie_parto_forceps%type;
ds_retorno_w		varchar(255) := null;

BEGIN

	begin
	select 	ie_parto_cesaria,
			ie_parto_normal,
			ie_laqueadura,
			ie_parto_forceps
	into STRICT	ie_parto_cesaria_w,
			ie_parto_normal_w,
			ie_laqueadura_w,
			ie_parto_forceps_w
	from	parto
	where	nr_atendimento =  nr_atendimento_p;
	exception
	when others then
		ie_parto_cesaria_w := 'N';
		ie_parto_normal_w := 'N';
		ie_laqueadura_w := 'N';
		ie_parto_forceps_w := 'N';
	end;

	if (ie_parto_cesaria_w = 'S') then
		ds_retorno_w := substr(OBTER_DESC_EXPRESSAO(284828),1,255);
	elsif (ie_parto_normal_w = 'S') then
		ds_retorno_w := substr(OBTER_DESC_EXPRESSAO(294264),1,255);
	elsif (ie_laqueadura_w = 'S') then
		ds_retorno_w := substr(OBTER_DESC_EXPRESSAO(292437),1,255);
	elsif (ie_parto_forceps_w = 'S') then
		ds_retorno_w := substr(OBTER_DESC_EXPRESSAO(290239),1,255);
	end if;

return	ds_retorno_w;

end;
