ie_parto_cesaria_w	parto.ie_parto_cesaria%type;
ds_retorno_w		varchar(255) := null;

BEGIN
if (coalesce(ie_sexo_p,'F') = 'F') then

	begin
	select 	ie_parto_cesaria,
		ie_parto_normal
