CREATE OR REPLACE FUNCTION obter_tipo_perda_ganho (nr_seq_tipo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(60);
ie_perda_ganho_w	varchar(20);
ds_grupo_w		varchar(40);
ds_cor_w		varchar(60);


BEGIN

select	
  CASE WHEN b.ie_perda_ganho='P' THEN obter_desc_expressao(295434,'Perda') WHEN b.ie_perda_ganho='G' THEN obter_desc_expressao(653120,'Ganho') WHEN b.ie_perda_ganho='J' THEN obter_desc_expressao(1032688,'Ganho Insensivel') WHEN b.ie_perda_ganho='I' THEN obter_desc_expressao(1032686,'Perda Insensivel')   END ,
	b.ie_perda_ganho,
	substr(b.ds_grupo,1,40),
	a.DS_COR
into STRICT	ds_retorno_w,
	ie_perda_ganho_w,
	ds_grupo_w,
	ds_cor_w
from	grupo_perda_ganho b,
	tipo_perda_ganho a
where	a.nr_sequencia	= nr_seq_tipo_p
and	a.nr_seq_grupo	= b.nr_sequencia;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= ie_perda_ganho_w;
elsif (ie_opcao_p = 'G') then
	ds_retorno_w	:= ds_grupo_w;
elsif (ie_opcao_p = 'COR') then
	ds_retorno_w	:= ds_cor_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
