CREATE OR REPLACE FUNCTION obter_tipo_pessoa_convenio (cd_convenio_p bigint) RETURNS bigint AS $body$
DECLARE


cd_tipo_pessoa_w	smallint	:= null;
cd_cgc_w		varchar(14);


BEGIN

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
	select	cd_cgc
	into STRICT	cd_cgc_w
	from	convenio
	where	cd_convenio	= cd_convenio_p;

	select	cd_tipo_pessoa
	into STRICT	cd_tipo_pessoa_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_w;
end if;

return cd_tipo_pessoa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
