CREATE OR REPLACE FUNCTION obter_tipo_pessoa_fis_precad (nr_seq_precad_p bigint, ie_tipo_pre_cadastro_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


retorno_w 	varchar(3) := null;


BEGIN							

	if (ie_tipo_pre_cadastro_p = 'FPF') then
		select cd_tipo_pj
		into STRICT retorno_w
		from pessoa_fisica_precad pa 
		where nr_sequencia = nr_seq_precad_p;
	elsif (ie_tipo_pre_cadastro_p = 'FPJ') then
		select cd_tipo_pessoa
		into STRICT retorno_w
		from pessoa_juridica_precad pa
		where nr_sequencia = nr_seq_precad_p;
	end if;
	
		
	return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
