CREATE OR REPLACE FUNCTION obter_tipo_pessoa_terceiro ( cd_medico_p text, dt_execucao_p timestamp, ie_tipo_terceiro_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_pf_pj_w		varchar(1) := null;
nr_seq_terceiro_w	terceiro.nr_sequencia%type;


BEGIN

if (ie_tipo_terceiro_p IS NOT NULL AND ie_tipo_terceiro_p::text <> '') then

	if (ie_tipo_terceiro_p = 'F') then
		select	max(trim(both 'F'))
		into STRICT	ie_pf_pj_w
		from	terceiro a
		where	a.cd_pessoa_fisica = cd_medico_p
		and	a.ie_situacao = 'A'
		and (coalesce(a.dt_fim_vigencia::text, '') = ''
			or
			a.dt_fim_vigencia > dt_execucao_p);

		if (coalesce(ie_pf_pj_w::text, '') = '') then
			select	max(a.nr_seq_terceiro)
			into STRICT	nr_seq_terceiro_w
			from	terceiro_pessoa_fisica a
			where	a.cd_pessoa_fisica = cd_medico_p
			and	exists (	SELECT	1
					from	terceiro x
					where	x.nr_sequencia = a.nr_seq_terceiro
					and	(x.cd_pessoa_fisica IS NOT NULL AND x.cd_pessoa_fisica::text <> '')
					and	x.ie_situacao = 'A'
					and (coalesce(x.dt_fim_vigencia::text, '') = ''
						or
						x.dt_fim_vigencia > dt_execucao_p))
			and (coalesce(a.dt_fim_vigencia::text, '') = ''
				or
				a.dt_fim_vigencia > dt_execucao_p);

			if (nr_seq_terceiro_w IS NOT NULL AND nr_seq_terceiro_w::text <> '') then
				select	max(trim(both 'F'))
				into STRICT	ie_pf_pj_w
				from	terceiro a
				where	nr_sequencia = nr_seq_terceiro_w
				and	a.ie_situacao = 'A'
				and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');
			end if;
		end if;
	else

		select	max(a.nr_seq_terceiro)
		into STRICT	nr_seq_terceiro_w
		from	terceiro_pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_medico_p
		and	exists (	SELECT	1
				from	terceiro x
				where	x.nr_sequencia = a.nr_seq_terceiro
				and	(x.cd_cgc IS NOT NULL AND x.cd_cgc::text <> '')
				and	x.ie_situacao = 'A'
				and (coalesce(x.dt_fim_vigencia::text, '') = ''
					or
					x.dt_fim_vigencia > dt_execucao_p))
		and (coalesce(a.dt_fim_vigencia::text, '') = ''
			or
			a.dt_fim_vigencia > dt_execucao_p);

		if (nr_seq_terceiro_w IS NOT NULL AND nr_seq_terceiro_w::text <> '') then
			select	max(trim(both 'J'))
			into STRICT	ie_pf_pj_w
			from	terceiro a
			where	nr_sequencia = nr_seq_terceiro_w
			and	a.ie_situacao = 'A'
			and	(cd_cgc IS NOT NULL AND cd_cgc::text <> '');
		end if;
	end if;
end if;

ds_retorno_w	:= ie_pf_pj_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
