SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_preco_simpro ( cd_simpro_p bigint, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_preco_w		varchar(01);
ds_tipo_preco_w		varchar(20);
ds_retorno_w			varchar(20);
cd_estab_simpro_w		estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

select	coalesce(max(ie_tipo_preco),'X'),
	CASE WHEN coalesce(max(ie_tipo_preco),'X')='F' THEN wheb_mensagem_pck.get_texto(802684) WHEN coalesce(max(ie_tipo_preco),'X')='V' THEN wheb_mensagem_pck.get_texto(802685)  ELSE wheb_mensagem_pck.get_texto(802686) END
into STRICT	ie_tipo_preco_w,
	ds_tipo_preco_w
from	simpro_preco
where	cd_simpro = cd_simpro_p
and     coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
and	dt_vigencia = (
	SELECT	max(dt_vigencia)
	from	simpro_preco
	where	cd_simpro = cd_simpro_p
	and     coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
	and	dt_vigencia <= clock_timestamp());

if (ie_cod_desc_p	= 'C') then
	ds_retorno_w	:= ie_tipo_preco_w;
elsif (ie_cod_desc_p	= 'D') then
	ds_retorno_w	:= ds_tipo_preco_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
