SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_principio ( nr_seq_principio_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_superior_w	bigint;
qt_registro_w		bigint;
ie_retorno_w		varchar(1);


BEGIN

/* Verifica o pai do princípio ativo  */

select	nr_seq_superior
into STRICT	nr_seq_superior_w
from	medic_ficha_tecnica
where	nr_sequencia	= nr_seq_principio_p;

/* Verifica se o princípio tem alguma derivada relacionada */

select	count(*)
into STRICT	qt_registro_w
from	medic_ficha_tecnica
where	nr_seq_superior	= nr_seq_principio_p;

/* Retorno */

if (qt_registro_w >= 0) and (coalesce(nr_seq_superior_w::text, '') = '') then
	ie_retorno_w	:= 'P';
else
	ie_retorno_w	:= 'D';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_principio ( nr_seq_principio_p bigint) FROM PUBLIC;

