CREATE OR REPLACE FUNCTION obter_tipo_procedimento ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


vl_result_w			varchar(90);
cd_tipo_proc_w		integer;
ds_tipo_proc_w		varchar(60);



BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select	cd_tipo_procedimento
	into STRICT	cd_tipo_proc_w
	from	procedimento
	where	cd_procedimento	= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;
	if (ie_cod_desc_p	<> 'C') then
		select	coalesce(ds_valor_dominio_cliente, ds_valor_dominio)
		into STRICT	ds_tipo_proc_w
		from	valor_dominio	
		where	cd_dominio 	= 95
		and	vl_dominio	= cd_tipo_proc_w;
	end if;
end if;

if (ie_cod_desc_p	= 'C') then
	vl_result_w		:= cd_tipo_proc_w;
else	
	vl_result_w		:= ds_tipo_proc_w;
end if;	

return	vl_result_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
